int worker(inv::INV_Redis* redis, const std::string& hash, int idx, const std::string& json, int batchSize, int sleep)
{
    const std::string prefix = "crush_uid_" + std::to_string(idx) + "_";
    int offset = 0;
    while(true)
    {
        std::vector<std::string> keys;
        std::vector<std::string> vals;

        for(int i = 0; i < batchSize; ++i)
        {
            keys.push_back(prefix + std::to_string(offset+i));
            vals.push_back(json);
        }
        offset += batchSize;
        int ret = RedisHashMSet(redis, hash, keys, vals);
        if (ret != 0)
        {
            fprintf(stderr, "RedisHashMSet fail: %d\n", batchSize);
        }

        usleep(sleep * 1000);
    }
    return 0;
}
